import React, { Component } from 'react'
import { Link } from 'react-router-dom'
import './styles/Badges.css'
import confLogo from '../images/badge-header.svg'
import BadgesList from '../components/BadgesList'
export class Badges extends Component {

    constructor(props){
      super(props)
      this.state = {
        data:[]
    }
      console.log('1. constructor')
    }
    componentDidMount(){
      console.log('3. componentDidMount')
        this.Timeout = setTimeout(()=>{
          this.setState({
            loading: true,
            error: null, 
              data: undefined,

              })
        },5000)

    }
    componentDidUpdate(prevProps,prevState){
      console.log('5.compoeupadte')
      console.log({
        prevProps: prevProps,
        prevState: prevState,
      })
      console.log({
        props:this.props,
        state: this.state,
      })
    }
    componentWillUnmount(){
      console.log("6. component willUnmount")
      clearTimeout(this.Timeout)
    }

    render() {
      console.log('2/4. render()')

      if(this.state.loading){
        return 'Loading...';
      }

        return (
            <React.Fragment>

               <div className="Badges">     
                    <div className="Badges__hero">
                        <div className="Badges__container">
                            <img className="Badges_conf-logo" src={confLogo} alt="logo"/>
                        </div>
                    </div>
               </div>
               <div className="Badges__container">
                   <div className="Badges_buttons">
                        <Link to="/badges/new" className="btn btn-primary">New File</Link>
                   </div>

                    <div className="Badges__list">
                         <div className="Badges__container">

                          <BadgesList Badge={this.state.data}/>

                            </div>

                     </div>
               </div>
            </React.Fragment>
        )
    }
}

export default Badges
