import React, { Component } from 'react'
import { Link } from 'react-router-dom'
import './styles/BadgesList.css'
import '../styles/Badge.css';

import Gravatar from './Gravatar'

function fetchRick(badges){
  const [query , setQuery ] = React.useState('')

  const [filteredBadges ,setFilteredBadges] = React.useState(badges)

   React.useMemo(
     ()=>{const result = badges.filter(badge =>{
    return` ${badge.firstName}${badge.lastName}`.toLowerCase().includes(query.toLowerCase())
   })

   setFilteredBadges(result)

},[badges, query]);
return{ query, setQuery, filteredBadges}
}

function fetchRick (props) {
     const badges = props.badges;

      const { query , setQuery, filteredBadges} = useSearchBadges(badges)



      if(filteredBadges.length === 0){
        return(
          <div>

            <h3>No badges were found</h3>
            <Link className="btn btn-primary" to="/badges/new">
              Create new badge
            </Link>
          </div>
        )
        }
  return (
      <div>
        <div className="form-group">
            <label>filter Badges</label>
            <input
              type="text" 
              className="form-control"
              value={query}
              onChange={(e)=>{
              setQuery(e.target.value)
            }}/>
        </div>  
          <ul className="Badgess">

            {filteredBadges.reverse().map((badges)=>{
              return (
                <li key={badges.id} className="Badge__li" >
                  <div className="Badge__imagen">
                   <img src={badges.img} alt=""/>
                  </div>
                  <Link className="text-reset text-decoration-none Badge__textos" to={`/badges/${badges.id}`}>

                  <div className="Badge__textos" >
                    <p>{badges.name} </p> 
                    <p>@{badges.status}</p>
                    <p>{badges.species}</p>
                  </div>

                </Link>
                </li>
                )
                })}
          </ul>
      </div>
        )

}

export default fetchRick
