import React, { Component } from 'react'
import { Link } from 'react-router-dom'
import './styles/BadgesList.css'
import '../styles/Badge.css';

import Gravatar from './Gravatar'


function BadgesList (props) {
     const badges = props.badge
      if(Badge.length === 0){
        return(
          <div>
            <h3>No badges were found</h3>
            <Link className="btn btn-primary" to="/badges/new">
              Create new badge
            </Link>
          </div>
        )
        }
  return (
      <div>
          <ul className="Badgess">

            {Badge.reverse().map((Badge)=>{
              return (
                <li key={Badge.id} className="Badge__li" >
                  <div className="Badge__imagen">
                    <Gravatar email={Badge.email} className="Badge__avatar" width="60" alt=""/>
                  </div>
                  <Link className="text-reset text-decoration-none Badge__textos" to={`/badges/${Badge.id}`}>



                  <div className="Badge__textos" >
                    <p>{Badge.firstName} {Badge.lastName}</p> 
                    <p>@{Badge.twitter}</p>
                    <p>{Badge.jobTitle}</p>
                  </div>

                </Link>
                </li>
                )
                })}
          </ul>
      </div>
        )

}

export default BadgesList
