import React, { Component } from 'react'
import '../styles/Badge.css';
import Skeleton, { SkeletonTheme } from '../components/skeleton'

import { Link } from 'react-router-dom'
import fetchRick from '../components/fetchrick'



export class BadgesRick extends React.Component {

    state = {
        loading: true,
        data: {
            results:[]
        },

}
    componentDidMount(){
        this.fetchData()

    }
    fetchData= async () =>{
        this.setState({loading: true, error: null})
     try {
         const response = await fetch(`https://rickandmortyapi.com/api/character`)
         const data = await response.json()
         this.setState({loading: false,data, error: null})
         console.log(data.results[0].name)
         console.log(this.state.data.results[0].name)

    }catch (error) {
        this.setState({loading: false, error})

     }
    }
    render() {
        if(this.state.loadig){
            return(<Skeleton />)
        }
        return (
            <ul  className="Badgess">


                            <div></div> 
                            {this.state.data.results.map(character =>(
                        <li key={character.id} className="Badge__li">
                            <div className="Badge__imagen">
                                <img src={character.image} alt=""/>
                            </div>
                            <div className="Badge__textos" >
                              <p>{character.name}</p> 
                                <p>@{character.status}</p>
                                <p>{character.species}</p>
                            </div>
                         </li> 
              ))}


            </ul>

        )
    }
}

export default BadgesRick
