import React, { Component } from 'react'

export class BadgesList extends Component {
    render() {
        return (
            <div>
                <ul className="list-unstyled" className="list-group">

                  {this.props.Badge.map((Badge)=>{
                                return (
                                <li key={Badge.id} className="list-group-item list-group-item-action flex-column align-items-start ">
                                    <div>
                                    <img src={Badge.avatarUrl} className="rounde-circle " width="60" alt=""/>
                                    </div>

                                   <p>{Badge.firstName} {Badge.lastName}</p> 
                                   <p>@{Badge.twitter}</p>
                                   <p>{Badge.jobTitle}</p>
                                </li>
                                )
                            })}
                </ul>
            </div>
        )
    }
}

export default BadgesList
