import React, { Component } from 'react'
import { Link } from 'react-router-dom'
import './styles/BadgesList.css'
import Gravatar from './Gravatar'
export class BadgesList extends Component {
    render() {

            if(this.props.Badge.length === 0){
              return(
                <div>
                  <h3>No badges were found</h3>
                  <Link className="btn btn-primary" to="/badges/new">
                    Create new badge
                  </Link>
                </div>
              )
            }
        return (
            <div>
                <ul className="Badgess">

                  {this.props.Badge.map((Badge)=>{
                    return (
                      <li key={Badge.id} className="Badge__li" >
                       <div className="Badge__imagen">
                          <Gravatar   email={this.props.badge.email} className="rounded-circle" width="60" alt=""/>
                        </div>
                        <div className="Badge__textos" >
                          <p>{Badge.firstName} {Badge.lastName}</p> 
                          <p>@{Badge.twitter}</p>
                          <p>{Badge.jobTitle}</p>
                        </div>

                      </li>
                      )
                      })}
                </ul>
            </div>
        )
    }
}

export default BadgesList
