import React, { Component } from 'react'
import { Link } from 'react-router-dom'
import './styles/Badges.css'
import confLogo from '../images/badge-header.svg'
import BadgesList from '../components/BadgesList'


import api from '../api'
export class Badges extends Component {

  constructor(props){
      super(props)
      this.state = {
            loading: true,
            error: null, 
            data: undefined,
    }
      console.log('1. constructor')
    }
    componentDidMount(){
      this.fetchData()

    }
    fetchData = async () =>{
      this.setState({loading: true, error: null})


      try{
        const data= await api.badges.list();
      this.setState({loading: false,data, error: null})
      }catch(error){
      this.setState({loading: false, error})

      }
    }

    render() {
      console.log('2/4. render()')

      if(this.state.loading){
        return 'Loading...';
      }
      if(this.state.error){
        return`erorr: ${this.state.error.message}` ;

      }

        return (
            <React.Fragment>

               <div className="Badges">     
                    <div className="Badges__hero">
                        <div className="Badges__container">
                            <img className="Badges_conf-logo" src={confLogo} alt="logo"/>
                        </div>
                    </div>
               </div>
               <div className="Badges__container">
                   <div className="Badges_buttons">
                        <Link to="/badges/new" className="btn btn-primary">New File</Link>
                   </div>

                    <div className="Badges__list">
                         <div className="Badges__container">

                          <BadgesList Badge={this.state.data}/>

                            </div>

                     </div>
               </div>
            </React.Fragment>
        )
    }
}

export default Badges
