import React from 'react'
import ReactDOM from 'react-dom'

function Modal(props) {

    if(!props.isOpen){
        return null;
    }
    return ReactDOM.createPortal(
        <div className="Modal">
            <h1>Modal</h1>
        </div>  
        ,
        document.getElementById('modal')
        );

}

export default Modal
