  constructor(props){
    super(props)
    this.state = {
          loading: true,
          error: null, 
          datar: undefined,

  }
  }
  fetchRick= async ()=>{

    try{
      const response= await fetch(`https://rickandmortyapi.com/api/character`);
      const datar = await response.json()
      this.setState({datar})
      console.log(datar)
    }catch(error){



    }
  }

  render() {
    return (
      <div>
          <div className="Badges">
