import React from 'react'

class BadgeForm extends React.Component{
    handleChange= e =>{
        // console.log(
        //     {name: e.target.name }
        //     ,{value: e.target.value})

        this.setState({
            [e.target.name]: e.target.value,
        })

    }
    handleClick= e =>{
        console.log('hola')
    }


    render(){
        return(
            <div>
                <h1>New attendant</h1>
                <form >
                    <div className="form_group">
                        <label >First Name</label>
                        <input onChange={this.handleChange} className="form-control" type="text" name="firstName" value={this.state.firstName}/>

                    </div>
                    <div className="form_group">
                        <label >Last Name</label>
                        <input onChange={this.handleChange} className="form-control" type="text" name="lastName" value={this.state.lastName}/>

                    </div>
                    <div className="form_group">
                        <label >eMail</label>
                        <input onChange={this.handleChange} className="form-control" type="text" name="email" value={this.state.email}/>

                    </div>
                    <div className="form_group">
                        <label >trabajo</label>
                        <input onChange={this.handleChange} className="form-control" type="text" name="jobTitle"/>

                    </div>
                    <div className="form_group">
                        <label >twita</label>
                        <input onChange={this.handleChange} className="form-control" type="text" name="twiter"/>

                    </div>
                    <button type="button" onClick={this.handleClick} className=" btn btn-primary">Save</button>
                </form>
            </div>
        )
    }
}

export default BadgeForm;
