import React, { Component } from 'react'
import './styles/BadgesList.css'
export class BadgesList extends Component {
    render() {
        return (
            <div>
                <ul className="Badge">

                  {this.props.Badge.map((Badge)=>{
                                return (
                                <li key={Badge.id} className="Badge__li">
                                    <div className="Badge__img">
                                    <img src={Badge.avatarUrl} className="rounded-circle" width="60" alt=""/>
                                    </div>
                                   <div className="Badge__textos" >
                                   <p>{Badge.firstName} {Badge.lastName}</p> 
                                   <p>@{Badge.twitter}</p>
                                   <p>{Badge.jobTitle}</p>
                                   </div>

                                </li>
                                )
                            })}
                </ul>
            </div>
        )
    }
}

export default BadgesList
