import React from 'react'
import md5 from 'md5'
function Gravatar(props) {
    const email = props.email;
    const hash = md5(email)
    return (


           render( <img src={`https://www.gravatar.com/avatar/${hash}?d=identicon`} className={this.props.className} alt=""/>
           )  


    )
}

export default Gravatar
