import React from 'react';
import md5 from 'md5';


function Gravatar(props) {
  email= props.email
  const hash = md5(email);

  return (
    <img

      src={`https://www.gravatar.com/avatar/${hash}?d=identicon`}
      alt="Avatar"
    />
  );

}

export default Gravatar;
