import React from 'react';

import '../styles/Badge.css'
import './styles/BadgeNew.css'

import BadgeForm from '../components/BadgeForm';
import header from '../images/platziconf-logo.svg'
import obito from '../images/Jeune_Obito_Uchiwa.jpg'
import Badge from '../components/Badge';

import api from '../api'

class BadgeNew extends React.Component{
    state={
        form: {
            firstName:'',
            lastName:'',
            email:'',
            jobTitle:'',
            twita: '',
        }
    }
    handleChange = e =>{
        this.setState({
            form:{
                ...this.state.form,
                [e.target.name]: e.target.value,
            }
        })
    }
    handleSubmit = async e =>{
        e.preventDefault();
        this.setState({loading: true,error:null})

        console.log('form was submiter')
        console.log(this.state)
        try{
            await api.badges.create(this.state.form)
            this.setState({loading: false})

        }catch(error){
            this.setState({loading: false,error})
        }
    }

    handleClick= e =>{
        console.log(this.state.form)
    }
    render(){
        return(
        <React.Fragment>

            <div className="BadgeNew__hero">
                <img className="img-fluid" src={header} alt="logo"/>

            </div>
            <div className="container">
                <div className="row">
                    <div className="col-6">
                        <Badge 
                        firstName={this.state.form.firstName || 'Nombre'}
                        lastName={this.state.form.lastName || 'Apellido'}
                        twiter={this.state.form.twita || 'Twitter'}
                        almohada="sisoy" 
                        jobTitle={this.state.form.jobTitle || 'trabajo'}
                        email={this.state.form.email}

                        urlImage={obito}
                        />
                    </div>
                    <div className="col-6">
                        <BadgeForm 
                        onChange={this.handleChange} 
                        onSubmit={this.handleSubmit}
                        onClick={this.handleClick}
                        formValue={this.state.form} />

                    </div>
                </div>
            </div>
        </React.Fragment>
            )
    }
}


export default BadgeNew;
