import React, { Component } from 'react'
import { Link } from 'react-router-dom'

import './styles/BadgesList.css'
export class BadgesList extends Component {
    render() {
        if(this.props.badges.lenght === 0){
            return(
                <div>
                    <h3>no encuentro  nada</h3>
                    <Link className="btn btn-primary" to='/badges/new' > crear un badge</Link>
                </div>
            )
        }
        return (
            <div>
                <ul className="Badges">

                  {this.props.Badge.map((Badge)=>{
                                return (
                                <li key={Badge.id} className="Badge__li" >
                                    <div className="Badge__imagen">
                                    <img src={Badge.avatarUrl} className="rounded-circle" width="60" alt=""/>
                                    </div>
                                   <div className="Badge__textos" >
                                   <p>{Badge.firstName} {Badge.lastName}</p> 
                                   <p>@{Badge.twitter}</p>
                                   <p>{Badge.jobTitle}</p>
                                   </div>

                                </li>
                                )
                            })}
                </ul>
            </div>
        )
    }
}

export default BadgesList
